// ************************************
// 3. HELPER COMPONENTS
// ************************************
function StatusChip({ status }) {
  let backgroundColor = "#eee";
  let textColor = "#000";
  switch (status) {
    case "Pending":
      backgroundColor = "#ffe8cc";
      textColor = "#d56500";
      break;
    case "Confirmed":
      backgroundColor = "#d5f5e3";
      textColor = "#186a3b";
      break;
    case "Cancelled":
      backgroundColor = "#f9cccc";
      textColor = "#b03a2e";
      break;
    default:
      backgroundColor = "#ddd";
      textColor = "#333";
  }
  return (
    <span
      style={{
        backgroundColor,
        color: textColor,
        padding: "4px 8px",
        borderRadius: "8px",
        fontSize: "0.85rem",
      }}
    >
      {status}
    </span>
  );
}

function CropIcon({ cropType }) {
  const map = {
