              {t(currentLanguage, "bookingDetails")}
            </button>
          </div>
        ))
      )}

      <h2>
        {t(currentLanguage, "myFields")} ({allFields.length})
      </h2>
      {allFields.length === 0 ? (
        <p>{t(currentLanguage, "noFields")}</p>
      ) : (
        allFields.map((field) => (
          <div
            key={field.id}
            style={{
              border: "1px solid #ccc",
              borderRadius: 8,
              padding: 8,
              marginBottom: 8,
            }}
          >
            <h3>
              <CropIcon cropType={field.cropType} />
              {field.name}
            </h3>
            <p>
              {field.size} ha - {field.cropType}
            </p>
            <p>
              {t(currentLanguage, "farmerPhone")}: {field.farmerMobile}
            </p>
            <button
              onClick={() => navigate(`/edit-field/${field.id}`)}
              style={{ marginRight: 6 }}
            >
              {t(currentLanguage, "editField")}
            </button>
            <button
              onClick={() => navigate(`/delete-field/${field.id}`)}
              style={{ marginRight: 6 }}
            >
              {t(currentLanguage, "deleteField")}
            </button>
          </div>
        ))
      )}

      <button onClick={() => navigate("/add-field")} style={{ marginTop: 12 }}>
        {t(currentLanguage, "addNewField")}
      </button>
    </div>
  );
}

// -- Add Field Screen --
function AddFieldScreen({ currentLanguage, onAddField, user }) {
  const navigate = useNavigate();
  const [fieldName, setFieldName] = useState("");
  const [fieldSize, setFieldSize] = useState("");
  const [cropType, setCropType] = useState("Corn");
  const [cadasterCode, setCadasterCode] = useState("");
  const [farmerMobile, setFarmerMobile] = useState("");

  const crops = ["Corn", "Wheat", "Barley", "Orchard", "Vegetables", "Sunflowers"];

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!fieldName || !fieldSize || !farmerMobile) {
      alert(
        t(currentLanguage, "fieldName") +
          " / " +
          t(currentLanguage, "fieldSize")
      );
      return;
    }
    const newField = {
      id: Date.now().toString(),
      farmerId: user.id,
      name: fieldName,
      size: parseFloat(fieldSize),
      cropType,
      farmerMobile,
      details: {
        fieldId: Date.now().toString(),
        cadasterCode,
        crop: {
          stemCount: 0,
          ndviIndex: 0,
          plantHeight: 0,
          plantDensity: 0,
          predictedYield: 0,
          multispectralData: {},
        },
        weatherForecast: [],
      },
    };
    onAddField(newField);
    navigate(-1);
  };

  return (
    <div style={{ padding: 16 }}>
      <BackButton />
      <h2>{t(currentLanguage, "addNewField")}</h2>
      <form onSubmit={handleSubmit} style={{ maxWidth: 400 }}>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "fieldName")}</label>
          <input
            type="text"
            value={fieldName}
            onChange={(e) => setFieldName(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "fieldSize")}</label>
          <input
            type="number"
            step="0.1"
            value={fieldSize}
            onChange={(e) => setFieldSize(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "cropType")}</label>
          <select
            value={cropType}
            onChange={(e) => setCropType(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          >
            {crops.map((c) => (
              <option key={c} value={c}>
                {c}
              </option>
            ))}
          </select>
        </div>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "cadasterCode")}</label>
          <input
            type="text"
            value={cadasterCode}
            onChange={(e) => setCadasterCode(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "farmerMobile")}</label>
          <input
            type="text"
            value={farmerMobile}
            onChange={(e) => setFarmerMobile(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          />
        </div>
        <button
          type="submit"
          style={{
            padding: "8px 16px",
            borderRadius: 6,
            background: "green",
            color: "#fff",
          }}
        >
          {t(currentLanguage, "addField")}
        </button>
      </form>
    </div>
  );
}

// -- Edit Field Screen --
function EditFieldScreen({ currentLanguage, fields, onUpdateField }) {
  const { id } = useParams();
  const navigate = useNavigate();
  const field = fields.find((f) => f.id === id);
  if (!field) {
    return (
      <div style={{ padding: 16 }}>
        <BackButton />
        <p>Field not found!</p>
      </div>
    );
  }

  const [fieldName, setFieldName] = useState(field.name);
  const [fieldSize, setFieldSize] = useState(field.size.toString());
  const [cropType, setCropType] = useState(field.cropType);
  const [cadasterCode, setCadasterCode] = useState(field.details.cadasterCode);
  const [farmerMobile, setFarmerMobile] = useState(field.farmerMobile);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!fieldName || !fieldSize || isNaN(fieldSize)) {
      alert(
        t(currentLanguage, "fieldName") +
          " / " +
          t(currentLanguage, "fieldSize")
      );
      return;
    }
    const updated = {
      ...field,
      name: fieldName,
      size: parseFloat(fieldSize),
      cropType,
      farmerMobile,
      details: {
        ...field.details,
        cadasterCode,
      },
    };
    onUpdateField(updated);
    navigate(-1);
  };

  const crops = ["Corn", "Wheat", "Barley", "Orchard", "Vegetables", "Sunflowers"];

  return (
    <div style={{ padding: 16 }}>
      <BackButton />
      <h2>{t(currentLanguage, "editFieldScreen")}</h2>
      <form onSubmit={handleSubmit} style={{ maxWidth: 400 }}>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "fieldName")}</label>
          <input
            type="text"
            value={fieldName}
            onChange={(e) => setFieldName(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "fieldSize")}</label>
          <input
            type="number"
            step="0.1"
            value={fieldSize}
            onChange={(e) => setFieldSize(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "cropType")}</label>
          <select
            value={cropType}
            onChange={(e) => setCropType(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          >
            {crops.map((c) => (
              <option key={c} value={c}>
                {c}
              </option>
            ))}
          </select>
        </div>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "cadasterCode")}</label>
          <input
            type="text"
            value={cadasterCode}
            onChange={(e) => setCadasterCode(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 8 }}>
          <label>{t(currentLanguage, "farmerMobile")}</label>
          <input
            type="text"
            value={farmerMobile}
            onChange={(e) => setFarmerMobile(e.target.value)}
            style={{ display: "block", width: "100%", marginTop: 4 }}
          />
        </div>
        <button
          type="submit"
          style={{
            padding: "8px 16px",
            borderRadius: 6,
            background: "orange",
            color: "#fff",
          }}
        >
          {t(currentLanguage, "saveChanges")}
        </button>
      </form>
    </div>
  );
}

// -- Delete Field Screen --
function DeleteFieldScreen({ currentLanguage, fields, onDeleteField, bookings }) {
  const { id } = useParams();
  const navigate = useNavigate();
  const field = fields.find((f) => f.id === id);
  if (!field) {
    return (
      <div style={{ padding: 16 }}>
        <BackButton />
        <p>Field not found!</p>
      </div>
    );
  }
  const hasActiveBooking = bookings.some(
    (b) =>
      b.field.id === field.id &&
      (b.status === "Pending" || b.status === "Confirmed")
  );

  const handleDelete = () => {
    onDeleteField(field.id);
    navigate(-1);
  };

  return (
    <div style={{ padding: 16 }}>
      <BackButton />
      <h2>{t(currentLanguage, "deleteField")}</h2>
      {hasActiveBooking ? (
        <p style={{ color: "red" }}>
          Cannot delete this fieldâ€”it has an active booking!
        </p>
      ) : (
        <>
          <p>{t(currentLanguage, "confirmDelete")}</p>
          <button
            onClick={handleDelete}
            style={{ marginRight: 8, color: "white", background: "red" }}
          >
            {t(currentLanguage, "deleteField")}
          </button>
        </>
      )}
    </div>
  );
}

// -- Booking Detail Screen --
function BookingDetailScreen({ currentLanguage, user, bookings, onUpdateBooking }) {
  const { id } = useParams();
  const navigate = useNavigate();
  const booking = bookings.find((b) => b.id === id);

  if (!booking) {
    return (
      <div style={{ padding: 16 }}>
        <BackButton />
        <p>Booking not found!</p>
      </div>
    );
  }

  const handleCancel = () => {
    const updated = { ...booking, status: "Cancelled" };
    onUpdateBooking(updated);
    alert(t(currentLanguage, "bookingCancelled"));
    navigate(-1);
  };

  const handleConfirmPendingToggle = () => {
    let newStatus = booking.status === "Pending" ? "Confirmed" : "Pending";
    const updated = { ...booking, status: newStatus };
    onUpdateBooking(updated);
    if (newStatus === "Confirmed") {
      alert(t(currentLanguage, "bookingConfirmed"));
    } else {
      alert(`${t(currentLanguage, "bookingSetTo")} ${newStatus}`);
    }
    navigate(-1);
  };

  return (
    <div style={{ padding: 16 }}>
      <BackButton />
      <h2>{t(currentLanguage, "bookingDetails")}</h2>
      <div style={{ border: "1px solid #ccc", borderRadius: 8, padding: 8 }}>
        <StatusChip status={booking.status} />
        <h3>{booking.field.name}</h3>
        <p>
          {booking.field.size} ha - {booking.field.cropType}
        </p>
        <p>
          {booking.dateRange.start.toLocaleString()} -{" "}
          {booking.dateRange.end.toLocaleString()}
        </p>
        <p>
          {t(currentLanguage, "sprayChemical")}: {booking.sprayChemical}
        </p>
        <p>
          {t(currentLanguage, "dosage")}: {booking.dosage} gal/acre
        </p>
        <p>
          {t(currentLanguage, "reason")}: {booking.problemReason}
        </p>
        <p>
          {t(currentLanguage, "priceGEL")}: {booking.price.toFixed(2)}
        </p>
        <hr />
        <h4>{t(currentLanguage, "fieldInformation")}</h4>
        <p>
          {t(currentLanguage, "farmerMobile")}: {booking.field.farmerMobile}
        </p>
        <p>
          {t(currentLanguage, "fileName")}: {booking.field.details.cadasterCode}
        </p>
      </div>
      <br />
      {booking.status !== "Cancelled" && (
        <button
          onClick={handleCancel}
          style={{ marginRight: 8, background: "red", color: "#fff" }}
        >
          {t(currentLanguage, "cancelled")}
        </button>
      )}
      {user.userType === UserType.OPERATOR && booking.status !== "Cancelled" && (
        <button
          onClick={handleConfirmPendingToggle}
          style={{ background: "green", color: "#fff" }}
        >
          {booking.status === "Pending"
            ? t(currentLanguage, "confirmed")
            : t(currentLanguage, "pending")}
        </button>
      )}
    </div>
  );
}

// ************************************
// 5. MAIN APP COMPONENT
// ************************************
function DroneApp() {
  const [currentLanguage, setCurrentLanguage] = useState(AppLanguage.GEORGIAN);
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [users, setUsers] = useState(initialUsers);
  const [fields, setFields] = useState(initialFields);
  const [bookings, setBookings] = useState(initialBookings);

  const toggleLanguage = () => {
    setCurrentLanguage((prev) =>
      prev === AppLanguage.GEORGIAN ? AppLanguage.ENGLISH : AppLanguage.GEORGIAN
    );
  };

  const handleLogin = (user) => {
    setLoggedInUser(user);
  };

  const handleLogout = () => {
    setLoggedInUser(null);
  };

  const handleAddField = (newField) => {
    setFields((prev) => [...prev, newField]);
  };

  const handleUpdateField = (updatedField) => {
    setFields((prev) =>
      prev.map((f) => (f.id === updatedField.id ? updatedField : f))
    );
  };

  const handleDeleteField = (fieldId) => {
    setFields((prev) => prev.filter((f) => f.id !== fieldId));
    setBookings((prev) => prev.filter((b) => b.field.id !== fieldId));
  };

  const handleAddBooking = (newBooking) => {
    setBookings((prev) => [...prev, newBooking]);
  };

  const handleUpdateBooking = (updated) => {
    setBookings((prev) => prev.map((b) => (b.id === updated.id ? updated : b)));
  };

  return (
    <Router>
      <Routes>
        {!loggedInUser ? (
          // If not logged in, show LoginScreen
          <Route
            path="*"
            element={
              <LoginScreen
                currentLanguage={currentLanguage}
                onLanguageToggle={toggleLanguage}
                onLogin={handleLogin}
                users={users}
              />
            }
          />
        ) : loggedInUser.userType === UserType.FARMER ? (
          // Farmer Routes
          <>
            <Route
              path="/"
              element={
                <FarmerDashboard
                  currentLanguage={currentLanguage}
                  onLanguageToggle={toggleLanguage}
                  onLogout={handleLogout}
                  user={loggedInUser}
                  fields={fields}
                  bookings={bookings}
                />
              }
            />
            <Route
              path="/add-field"
              element={
                <AddFieldScreen
                  currentLanguage={currentLanguage}
                  user={loggedInUser}
                  onAddField={handleAddField}
                />
              }
            />
            <Route
              path="/edit-field/:id"
              element={
                <EditFieldScreen
                  currentLanguage={currentLanguage}
                  fields={fields}
                  onUpdateField={handleUpdateField}
                />
              }
            />
            <Route
              path="/delete-field/:id"
              element={
                <DeleteFieldScreen
                  currentLanguage={currentLanguage}
                  fields={fields}
                  bookings={bookings}
                  onDeleteField={handleDeleteField}
                />
              }
            />
            <Route
              path="/booking/:id"
              element={
                <BookingDetailScreen
                  currentLanguage={currentLanguage}
                  user={loggedInUser}
                  bookings={bookings}
                  onUpdateBooking={handleUpdateBooking}
                />
              }
            />
            <Route
              path="/request-spray/:fieldId"
              element={
                <RequestSprayScreen
                  currentLanguage={currentLanguage}
                  user={loggedInUser}
                  fields={fields}
                  onAddBooking={handleAddBooking}
                />
              }
            />
            {/* Catch-all to FarmerDashboard */}
            <Route
              path="*"
              element={
                <FarmerDashboard
                  currentLanguage={currentLanguage}
                  onLanguageToggle={toggleLanguage}
                  onLogout={handleLogout}
                  user={loggedInUser}
                  fields={fields}
                  bookings={bookings}
                />
              }
            />
          </>
        ) : (
          // Operator Routes
          <>
            <Route
              path="/"
              element={
                <OperatorDashboard
                  currentLanguage={currentLanguage}
                  onLanguageToggle={toggleLanguage}
                  onLogout={handleLogout}
                  user={loggedInUser}
                  fields={fields}
                  bookings={bookings}
                />
              }
            />
            <Route
              path="/add-field"
              element={
                <AddFieldScreen
                  currentLanguage={currentLanguage}
                  user={loggedInUser}
                  onAddField={handleAddField}
                />
              }
            />
            <Route
              path="/edit-field/:id"
              element={
                <EditFieldScreen
                  currentLanguage={currentLanguage}
                  fields={fields}
                  onUpdateField={handleUpdateField}
                />
              }
            />
            <Route
              path="/delete-field/:id"
              element={
                <DeleteFieldScreen
                  currentLanguage={currentLanguage}
                  fields={fields}
                  bookings={bookings}
                  onDeleteField={handleDeleteField}
                />
              }
            />
            <Route
              path="/booking/:id"
              element={
                <BookingDetailScreen
                  currentLanguage={currentLanguage}
                  user={loggedInUser}
                  bookings={bookings}
                  onUpdateBooking={handleUpdateBooking}
                />
              }
            />
            <Route
              path="/request-spray/:fieldId"
              element={
                <RequestSprayScreen
                  currentLanguage={currentLanguage}
                  user={loggedInUser}
                  fields={fields}
                  onAddBooking={handleAddBooking}
                />
              }
            />
            {/* Catch-all to OperatorDashboard */}
            <Route
              path="*"
              element={
                <OperatorDashboard
                  currentLanguage={currentLanguage}
                  onLanguageToggle={toggleLanguage}
                  onLogout={handleLogout}
                  user={loggedInUser}
                  fields={fields}
                  bookings={bookings}
                />
              }
            />
          </>
        )}
      </Routes>
    </Router>
  );
}

// ************************************
// 6. Finally, mount to #root
// ************************************
const rootElement = document.getElementById("root");
const root = ReactDOM.createRoot(rootElement);
root.render(<DroneApp />);
